package corejavabasicssamples;

public class operatorsss {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=5;
		System.out.println("a+b="+(a+b));
		System.out.println("a-b="+(a-b));
		System.out.println("a*b="+(a*b));
		System.out.println("a/b="+(a/b));
		System.out.println("a%b="+(a%b));
		int ac=4;
		int var;
		var=ac;
		System.out.println("var using =:"+var);
		var+=ac;
		System.out.println("var using +=:"+var);
		var*=ac;
		System.out.println("var using *=:"+var);
	}

}
